import { useContext } from 'react';
import { AuthContext } from './authContext';
import { If } from 'react-if';

function IsAuthorized (props){
    let context = useContext(AuthContext);
    let loggedIn = context.loggedIn;

    return (
        <If condition={loggedIn}>
            {props.children}
        </If>
    )

}
