import { useContext, useEffect, useState } from 'react';
import { ListContext } from '../../context/listContex';
import { Button, Icon } from '@blueprintjs/core';

const DisplayTasks = () => {
    const context = useContext(ListContext);
    let list = context.list;
    let setList = context.setList;
    let pagination = context.pagination;
    let [renderedTasks, renderTasks] = useState([])
    let [index, setIndex] = useState(0);
    let [buttonActive, setButtonActive] = useState({
      left:"false",
      right:"false",
    })

function deleteItem(id) {
    const items = list.filter( item => item.id !== id );
    setList(items);
    }

function showTasks(index) {
  return renderedTasks.slice(index, index+pagination);
}

function paginationAdd(){
  if(!index+pagination + pagination){
    setButtonActive({
      left,
      right:'disabled'
    })
  }
setIndex(index + pagination);
}
function paginationSubtract(){
if(index - pagination <= 0){
  setIndex(index - pagination);
} else { setIndex(0)}
}

useEffect(() => {
  console.log(list)
    let tasks = list.map(item => (
        <div key={item.id}>
          <p>{item.text}</p>
          <p><small>Assigned to: {item.assignee}</small></p>
          <p><small>Difficulty: {item.difficulty}</small></p>
          <div onClick={() => toggleComplete(item.id)}>Complete: {item.complete.toString()}</div>
          <hr />
          <Button intent='success' text='Complete Task'></Button>
          <Button intent='danger' text='X' onClick={() => deleteItem(item.id)}></Button>
        </div>
      ))
    renderTasks(tasks);
}, [list])

return(<>
{showTasks(index)}
<Button rightIcon='arrow-left' onClick={() => paginationSubtract()} disabled={buttonActive.left}></Button>
<Button rightIcon='arrow-right' onClick={() => paginationAdd()} disabled={buttonActive.right}></Button>
  </>)
}

export default DisplayTasks;
