import { useContext, useEffect, useState } from 'react';
import { ListContext } from '../../context/listContex';
import { Button, Icon } from '@blueprintjs/core';
import { arrayLengthCompare } from '@blueprintjs/core/lib/esm/common/utils';

const DisplayTasks = () => {
    const context = useContext(ListContext);
    let list = context.list;
    let setList = context.setList;
    let pagination = context.pagination;
    let [index, setIndex] = useState(0);
    let [showButtonLeft, setShowButtonLeft] = useState('false')
    let [showButtonRight, setShowButtonRight] = useState('false')

function deleteItem(id) {
    const items = list.filter( item => item.id !== id );
    setList(items);
    }

function showTask(idx){
  let task = list.slice(idx, idx + pagination);
  return task.map(item => (
          <div key={item.id}>
            <p>{item.text}</p>
            <p><small>Assigned to: {item.assignee}</small></p>
            <p><small>Difficulty: {item.difficulty}</small></p>
            <div onClick={() => toggleComplete(item.id)}>Complete: {item.complete.toString()}</div>
            <hr />
            <Button intent='success' text='Complete Task'></Button>
            <Button intent='danger' text='X' onClick={() => deleteItem(item.id)}></Button>
           </div>
        ))
}
useEffect(() => {
  console.log(index + pagination, list.length -1)
if(index + pagination > list.length -1){
  setShowButtonRight('false');
}
if(index + pagination < list.length - 1){
  setShowButtonRight('true')
  console.log('right set to ',showButtonRight )
  }
if(index === 0){
  setShowButtonLeft('false')
}
  if(index !== 0){
    setShowButtonLeft('true')
  }
}, [list])

function paginationAdd(){
  setIndex( index + pagination);
}
function paginationSubtract(){
  setIndex( index - pagination);
}

