import { useState, createContext, useEffect} from 'react';

export const ListContext = createContext();

function ListContextProvider(props){
    const [ list, setList]  = useState([])
    const [ incomplete, setIncomplete] = useState([])
    const [ pagination, setPagination] = useState(3)
    const [index, setIndex] = useState(0);
    const [ sort, setSort] = useState()
    const [showComplete, setShowComplete] = useState(false)

    useEffect(() => {
        localStorage.setItem('settings', JSON.stringify({ pagination, sort, showComplete }));
    }, [pagination, sort, showComplete])
 
    return (
        <ListContext.Provider value={{ list, setList, incomplete, setIncomplete, pagination, setPagination, index, setIndex, sort, setSort, showComplete, setShowComplete}}>
            {props.children}
        </ListContext.Provider>
    )
}

export default ListContextProvider;
